import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../AuthContext";
import api from "../api";

export default function Dashboard() {
  const { user, logout } = useContext(AuthContext);
  const [locations, setLocations] = useState([]);
  const [cityName, setCityName] = useState("");
  const [label, setLabel] = useState("");
  const [notes, setNotes] = useState("");
  const [error, setError] = useState("");

  const fetchLocations = async () => {
    try {
      const res = await api.get("/locations");
      setLocations(res.data);
    } catch (err) {
      console.error(err);
      setError("Failed to load locations.");
    }
  };

  useEffect(() => {
    fetchLocations();
  }, []);

  const handleAddLocation = async (e) => {
    e.preventDefault();
    setError("");
    try {
      const res = await api.post("/locations", { cityName, label, notes });
      setLocations([...locations, res.data]);
      setCityName("");
      setLabel("");
      setNotes("");
    } catch (err) {
      console.error(err);
      setError("Failed to add location.");
    }
  };

  const handleDeleteLocation = async (id) => {
    try {
      await api.delete(`/locations/${id}`);
      setLocations(locations.filter((loc) => loc.id !== id));
    } catch (err) {
      console.error(err);
      setError("Failed to delete location.");
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h2>Weather Dashboard</h2>
        <div>
          <span style={{ marginRight: "10px" }}>
            Logged in as: <strong>{user?.username}</strong>
          </span>
          <button onClick={logout} style={styles.logoutBtn}>
            Logout
          </button>
        </div>
      </div>

      {error && <p style={{ color: "red" }}>{error}</p>}

      <section style={styles.section}>
        <h3>Add Weather Location</h3>
        <form onSubmit={handleAddLocation} style={styles.form}>
          <input
            style={styles.input}
            placeholder="City Name"
            value={cityName}
            onChange={(e) => setCityName(e.target.value)}
            required
          />
          <input
            style={styles.input}
            placeholder="Label (Home, Office...)"
            value={label}
            onChange={(e) => setLabel(e.target.value)}
          />
          <input
            style={styles.input}
            placeholder="Notes"
            value={notes}
            onChange={(e) => setNotes(e.target.value)}
          />
          <button style={styles.button} type="submit">
            Add
          </button>
        </form>
      </section>

      <section style={styles.section}>
        <h3>Your Saved Locations</h3>
        {locations.length === 0 ? (
          <p>No locations yet. Add one above.</p>
        ) : (
          <ul style={styles.list}>
            {locations.map((loc) => (
              <li key={loc.id} style={styles.listItem}>
                <div>
                  <strong>{loc.cityName}</strong>{" "}
                  {loc.label && <span>({loc.label})</span>}
                  {loc.notes && (
                    <div style={styles.notes}>Notes: {loc.notes}</div>
                  )}
                </div>
                <button
                  style={styles.deleteBtn}
                  onClick={() => handleDeleteLocation(loc.id)}
                >
                  Delete
                </button>
              </li>
            ))}
          </ul>
        )}
      </section>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: "800px",
    margin: "40px auto",
    padding: "20px",
    border: "1px solid #ddd",
    borderRadius: "8px",
    fontFamily: "Arial, sans-serif",
  },
  header: {
    display: "flex",
    justifyContent: "space-between",
    marginBottom: "20px",
    alignItems: "center",
  },
  logoutBtn: {
    padding: "6px 10px",
    cursor: "pointer",
  },
  section: {
    marginBottom: "20px",
  },
  form: {
    display: "flex",
    gap: "8px",
    flexWrap: "wrap",
  },
  input: {
    padding: "8px",
    minWidth: "150px",
    flex: "1 1 150px",
  },
  button: {
    padding: "8px 16px",
    cursor: "pointer",
  },
  list: {
    listStyle: "none",
    padding: 0,
    marginTop: "10px",
  },
  listItem: {
    display: "flex",
    justifyContent: "space-between",
    padding: "10px",
    borderBottom: "1px solid #eee",
    alignItems: "center",
  },
  deleteBtn: {
    padding: "6px 10px",
    cursor: "pointer",
  },
  notes: {
    fontSize: "12px",
    color: "#555",
  },
};
