import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../AuthContext";
import api from "../api";

const OPENWEATHER_API_KEY = ""; // keep your key

export default function Dashboard() {
  const { user, logout } = useContext(AuthContext);

  const [locations, setLocations] = useState([]);
  const [cityName, setCityName] = useState("");
  const [label, setLabel] = useState("");
  const [notes, setNotes] = useState("");

  const [searchCity, setSearchCity] = useState("");
  const [weatherResult, setWeatherResult] = useState(null);
  const [weatherError, setWeatherError] = useState("");

  const [error, setError] = useState("");

  const fetchLocations = async () => {
    try {
      const res = await api.get("/locations");
      setLocations(res.data);
    } catch (err) {
      console.error(err);
      setError("Failed to load saved locations.");
    }
  };

  useEffect(() => {
    fetchLocations();
  }, []);

  const handleAddLocation = async (e) => {
    e.preventDefault();
    setError("");
    try {
      const res = await api.post("/locations", { cityName, label, notes });
      setLocations([...locations, res.data]);
      setCityName("");
      setLabel("");
      setNotes("");
    } catch (err) {
      console.error(err);
      setError("Failed to add location.");
    }
  };

  const handleDeleteLocation = async (id) => {
    try {
      await api.delete(`/locations/${id}`);
      setLocations(locations.filter((loc) => loc.id !== id));
    } catch (err) {
      console.error(err);
      setError("Failed to delete location.");
    }
  };

  const handleSearchWeather = async (e) => {
    e.preventDefault();
    setWeatherError("");
    setWeatherResult(null);

    if (!searchCity.trim()) {
      setWeatherError("Please enter a city name.");
      return;
    }

    try {
      const response = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${encodeURIComponent(
          searchCity
        )}&units=metric&appid=${OPENWEATHER_API_KEY}`
      );

      if (!response.ok) {
        if (response.status === 404) {
          setWeatherError("City not found. Try another name.");
        } else {
          setWeatherError("Failed to fetch weather. Try again.");
        }
        return;
      }

      const data = await response.json();
      const result = {
        city: data.name,
        country: data.sys?.country,
        temp: data.main?.temp,
        feelsLike: data.main?.feels_like,
        description: data.weather?.[0]?.description,
      };
      setWeatherResult(result);
    } catch (err) {
      console.error(err);
      setWeatherError("Network error while fetching weather.");
    }
  };

  return (
    <div className="app-shell">
      <div className="card">
        <header className="dashboard-header">
          <div>
            <div className="badge">Weather App</div>
            <h2 className="dashboard-title">Search & Save Locations</h2>
            <p className="card-subtitle">
              Find temperature for any city and keep a list of your favourites.
            </p>
          </div>

          <div style={{ display: "flex", gap: "8px", alignItems: "center" }}>
            <div className="user-chip">
              <span>ðŸ‘¤</span>
              <span>{user?.username}</span>
            </div>
            <button className="btn btn-outline" onClick={logout}>
              Logout
            </button>
          </div>
        </header>

        {error && <p className="text-error">{error}</p>}

        <div className="dashboard-grid">
          {/* LEFT: Weather search */}
          <section className="section">
            <div className="section-header">
              <h3 className="section-title">Search weather by city</h3>
            </div>

            <form className="search-form" onSubmit={handleSearchWeather}>
              <input
                className="input"
                placeholder="Enter city (e.g., Hyderabad)"
                value={searchCity}
                onChange={(e) => setSearchCity(e.target.value)}
              />
              <button className="btn btn-primary" type="submit">
                Search
              </button>
            </form>

            {weatherError && <p className="text-error">{weatherError}</p>}

            {weatherResult && (
              <div className="weather-card">
                <div className="weather-city">
                  {weatherResult.city}
                  {weatherResult.country && `, ${weatherResult.country}`}
                </div>
                <div className="weather-temp">
                  {Math.round(weatherResult.temp)}Â°C
                </div>
                {weatherResult.description && (
                  <div className="weather-desc">
                    {weatherResult.description}
                  </div>
                )}
                {weatherResult.feelsLike !== undefined && (
                  <p className="text-muted">
                    Feels like: {Math.round(weatherResult.feelsLike)}Â°C
                  </p>
                )}
              </div>
            )}
          </section>

          {/* RIGHT: Saved locations */}
          <section className="section">
            <div className="section-header">
              <h3 className="section-title">Saved locations</h3>
            </div>

            <form className="form" onSubmit={handleAddLocation}>
              <input
                className="input"
                placeholder="City Name"
                value={cityName}
                onChange={(e) => setCityName(e.target.value)}
                required
              />
              <input
                className="input"
                placeholder="Label (Home, Office...)"
                value={label}
                onChange={(e) => setLabel(e.target.value)}
              />
              <input
                className="input"
                placeholder="Notes"
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
              />
              <button className="btn btn-primary" type="submit">
                Save location
              </button>
            </form>

            <ul className="locations-list">
              {locations.length === 0 ? (
                <p className="text-muted" style={{ marginTop: "8px" }}>
                  No saved locations yet.
                </p>
              ) : (
                locations.map((loc) => (
                  <li key={loc.id} className="location-item">
                    <div className="location-main">
                      <span className="location-title">
                        {loc.cityName} {loc.label && `(${loc.label})`}
                      </span>
                      {loc.notes && (
                        <span className="location-sub">{loc.notes}</span>
                      )}
                    </div>
                    <button
                      className="btn btn-danger"
                      type="button"
                      onClick={() => handleDeleteLocation(loc.id)}
                    >
                      Delete
                    </button>
                  </li>
                ))
              )}
            </ul>
          </section>
        </div>
      </div>
    </div>
  );
}
